// Get connected MACs from UniFi
const wifiClients = $('Get WiFi Clients').first().json.data || [];
const connectedMacs = new Set(wifiClients.map(c => c.macAddress?.toLowerCase()));

// Get employees
const employees = $('Get Employees with MACs').all();

// Get current date and time (Pacific Time) - CHANGE TIMEZONE AS NEEDED
const now = new Date();
const pacificOptions = { timeZone: 'America/Los_Angeles' };
const pacificDate = now.toLocaleDateString('en-CA', pacificOptions);
const currentHour = parseInt(now.toLocaleTimeString('en-US', { ...pacificOptions, hour: 'numeric', hour12: false }));
const dayOfWeek = now.toLocaleDateString('en-US', { ...pacificOptions, weekday: 'long' });

// Determine greeting based on time
let timeOfDay;
if (currentHour >= 5 && currentHour < 12) {
  timeOfDay = "morning";
} else if (currentHour >= 12 && currentHour < 17) {
  timeOfDay = "afternoon";
} else {
  timeOfDay = "evening";
}

// Find employees to greet
const toGreet = [];

for (const emp of employees) {
  const mac = emp.json.employee_mac?.toLowerCase();
  const lastGreeted = emp.json.last_greeted;

  if (mac && connectedMacs.has(mac)) {
    let lastGreetedPacific = null;
    if (lastGreeted) {
      const greetedDate = new Date(lastGreeted);
      lastGreetedPacific = greetedDate.toLocaleDateString('en-CA', pacificOptions);
    }

    if (lastGreetedPacific !== pacificDate) {
      toGreet.push({
        json: {
          contact_id: emp.json.contact_id,
          first_name: emp.json.first_name,
          last_name: emp.json.last_name,
          phone: emp.json.phone,
          employee_mac: mac,
          today: pacificDate,
          timeOfDay: timeOfDay,
          dayOfWeek: dayOfWeek
        }
      });
    }
  }
}

return toGreet;

// Get the current employee from the loop
const employee = $('Loop Over Arrivals').first().json;

// Get past greetings
const pastGreetings = $('Get Recent Greetings').all()
  .map(g => g.json.greeting_text)
  .filter(t => t)
  .map(t => '- ' + t)
  .join('\n') || '(None)';

return [{
  json: {
    ...employee,
    pastGreetings: pastGreetings
  }
}];
